mapper.select:
select a.*
from hm_txn_stl a
where a.txn_date = #{prevDate} and a.txac_brid = #{deptCode}
and a.txn_sn not in (select fund_txn_sn from hm_txn_vch b where b.txn_date=#{prevDate}
and b.txac_brid = #{deptCode})

select a.*
from hm_txn_stl a
where a.txn_date = #{currentDate} and a.opr1_no = #{operCode}
and a.txn_sn not in (select fund_txn_sn from hm_txn_vch b where b.txn_date=#{currentDate}
and b.opr1_no = #{operCode})
